mapper.select:
select d.Donate_ID,d.Address_ID,d.Scheduled_Time,d.Finished_Time,d.Donate_Detail,d.Evaluation_ID,c.Collector_Name,c.Phone,h.Help_Name,a.Address_Details from donation d left join collector c on d.Collector_Person_ID=c.Collector_Person_ID left join help_the_poor h on d.Help_ID=h.Help_ID left join address a on d.Address_ID=a.Address_ID where d.User_ID= #{userid,jdbcType=INTEGER}
and c.Collector_Name like "%"#{collectorname,jdbcType=VARCHAR}"%"
and DATE_FORMAT(d.Scheduled_Time, '%Y-%m-%d')=#{date}
and DATE_FORMAT(d.Scheduled_Time, '%Y-%m')=#{date}
order by d.Scheduled_Time asc

select d.Donate_ID,d.Address_ID,d.Scheduled_Time,d.Donate_Detail,c.Collector_Name,c.Phone,h.Help_Name,a.Address_Details from donation d left join collector c on d.Collector_Person_ID=c.Collector_Person_ID left join help_the_poor h on d.Help_ID=h.Help_ID left join address a on d.Address_ID=a.Address_ID where d.User_ID= #{userid,jdbcType=INTEGER} and d.Finished_Time is null
and c.Collector_Name like "%"#{collectorname,jdbcType=VARCHAR}"%"
and DATE_FORMAT(d.Scheduled_Time, '%Y-%m-%d')=#{date}
and DATE_FORMAT(d.Scheduled_Time, '%Y-%m')=#{date}
order by d.Scheduled_Time asc

select d.Donate_ID,d.Address_ID,d.Scheduled_Time,d.Finished_Time,d.Donate_Detail,d.Evaluation_ID,u.User_Name,u.Phone,h.Help_Name,a.Address_Details from donation d left join user u on d.User_ID=u.User_ID left join help_the_poor h on d.Help_ID=h.Help_ID left join address a on d.Address_ID=a.Address_ID where d.Collector_Person_ID= #{collectorid,jdbcType=INTEGER}
and u.User_Name like "%"#{username,jdbcType=VARCHAR}"%"
and DATE_FORMAT(d.Scheduled_Time, '%Y-%m-%d')=#{date}
and DATE_FORMAT(d.Scheduled_Time, '%Y-%m')=#{date}
order by d.Scheduled_Time asc

select d.Donate_ID,d.Address_ID,d.Scheduled_Time,d.Donate_Detail,d.Evaluation_ID,u.User_Name,u.Phone,h.Help_Name,a.Address_Details from donation d left join user u on d.User_ID=u.User_ID left join help_the_poor h on d.Help_ID=h.Help_ID left join address a on d.Address_ID=a.Address_ID where d.Collector_Person_ID= #{collectorid,jdbcType=INTEGER} and d.Finished_Time is null
and u.User_Name like "%"#{username,jdbcType=VARCHAR}"%"
and DATE_FORMAT(d.Scheduled_Time, '%Y-%m-%d')=#{date}
and DATE_FORMAT(d.Scheduled_Time, '%Y-%m')=#{date}
order by d.Scheduled_Time asc

select d.Donate_ID,d.Address_ID,d.Scheduled_Time,d.Finished_Time,u.User_Name,Donate_Detail,d.Evaluation_ID,c.Collector_Name,h.Help_Name,a.Address_Details from donation d left join collector c on d.Collector_Person_ID=c.Collector_Person_ID left join user u on d.User_ID=u.User_ID left join help_the_poor h on d.Help_ID=h.Help_ID left join address a on d.Address_ID=a.Address_ID where d.Donate_ID is not null
and u.User_Name like "%"#{username,jdbcType=VARCHAR}"%"
and c.Collector_Name like "%"#{collectorname,jdbcType=VARCHAR}"%"
and DATE_FORMAT(d.Scheduled_Time, '%Y-%m-%d')=#{date}
and DATE_FORMAT(d.Scheduled_Time, '%Y-%m')=#{date}
order by d.Scheduled_Time asc

select d.Donate_ID,d.Address_ID,d.Scheduled_Time,d.Donate_Detail,d.Evaluation_ID,c.Collector_Name,u.User_Name,h.Help_Name,a.Address_Details from donation d left join collector c on d.Collector_Person_ID=c.Collector_Person_ID left join user u on d.User_ID=u.User_ID left join help_the_poor h on d.Help_ID=h.Help_ID left join address a on d.Address_ID=a.Address_ID where d.Finished_Time is null
and u.User_Name like "%"#{username,jdbcType=VARCHAR}"%"
and c.Collector_Name like "%"#{collectorname,jdbcType=VARCHAR}"%"
and DATE_FORMAT(d.Scheduled_Time, '%Y-%m-%d')=#{date}
and DATE_FORMAT(d.Scheduled_Time, '%Y-%m')=#{date}
order by d.Scheduled_Time asc

mapper.update:
update donation
set
Collector_Person_ID = #{collectorid,jdbcType=INTEGER}
where Donate_ID = #{donateId,jdbcType=INTEGER}
