mapper.select:
select r.Recycle_Order_ID,r.Scheduled_Time,r.Finished_Time,r.Evaluation_ID,r.Address_ID,a.Address_Details,r.Total_Amount,c.Collector_Name,u.User_Name from recycle_orders r left join user u on r.User_ID=u.User_ID left join collector c on r.Collector_Person_ID=c.Collector_Person_ID left join address a on r.Address_ID=a.Address_ID where r.Recycle_Order_ID is not null
and u.User_Name like "%"#{username,jdbcType=VARCHAR}"%"
and c.Collector_Name like "%"#{collectorname,jdbcType=VARCHAR}"%"
and DATE_FORMAT(r.Scheduled_Time, '%Y-%m-%d')=#{date}
and DATE_FORMAT(r.Scheduled_Time, '%Y-%m')=#{date}
order by r.Scheduled_Time asc

select r.Recycle_Order_ID,r.Scheduled_Time,r.Address_ID,r.Total_Amount,c.Collector_Name,u.User_Name,a.Address_Details from recycle_orders r left join user u on r.User_ID=u.User_ID left join collector c on r.Collector_Person_ID=c.Collector_Person_ID left join address a on r.Address_ID=a.Address_ID where r.Finished_Time is null
and u.User_Name like "%"#{username,jdbcType=VARCHAR}"%"
and c.Collector_Name like "%"#{collectorname,jdbcType=VARCHAR}"%"
and DATE_FORMAT(r.Scheduled_Time, '%Y-%m-%d')=#{date}
and DATE_FORMAT(r.Scheduled_Time, '%Y-%m')=#{date}
order by r.Scheduled_Time asc

